mapper.update:
update business_place
place_code = #{placeCode}, place_name = #{placeName}, place_alias_name = #{placeAliasName}, place_type = #{placeType}, place_linkman = #{placeLinkman}, place_tel = #{placeTel}, place_email = #{placeEmail}, place_open_bank = #{placeOpenBank}, place_bank_account = #{placeBankAccount}, place_star_level = #{placeStarLevel}, place_status = #{placeStatus}, regist_time = #{registTime}, user_id = #{userId}, is_delete = #{isDelete}, remark = #{remark}, create_time = #{createTime}, create_by = #{createBy}, update_by = #{updateBy}, update_time = #{updateTime}, user_name = #{userName},
where place_id = #{placeId}

