mapper.select:


    is_deleted=1
     and project_code = #{projectCode}
     and project_name like concat('%', #{projectName}, '%')
     and project_trade = #{projectTrade}
     and project_owner = #{projectOwner}
     and purch_company = #{purchCompany}
     and project_total_amount = #{projectTotalAmount}
     and project_leader = #{projectLeader}
     and project_gross_margin = #{projectGrossMargin}
     and project_gross_profit = #{projectGrossProfit}
     and project_net_profit = #{projectNetProfit}
     and project_package_amount = #{projectPackageAmount}
     and project_desc = #{projectDesc}
     and project_status = #{projectStatus}
     and is_deleted = #{isDeleted}
     and check_content = #{checkContent}
     and file_name like concat('%', #{fileName}, '%')

        AND date_format(project_time_start,'%Y-%m-%d %H:%i:%s') >= date_format(#{start},'%Y-%m-%d %H:%i:%s')


        AND date_format(project_time_start,'%Y-%m-%d %H:%i:%s') <= date_format(#{end},'%Y-%m-%d %H:%i:%s')

     and project_check_status = #{projectCheckStatus}
     and project_ticket_status = #{projectTicketStatus}
     and project_contract_status = #{projectContractStatus}
     and project_acceptance_status = #{projectAcceptanceStatus}
     and project_dutypaid_status = #{projectDutypaidStatus}
     and place_code = #{placeCode}
     and purch_company_taxid = #{purchCompanyTaxid}
     and self_name = #{selfName}
     and ticket_type = #{ticketType}
     and ticket_tax = #{ticketTax}

ORDER BY
create_time DESC

mapper.update:
update self_project
project_code = #{projectCode}, project_name = #{projectName}, project_trade = #{projectTrade}, project_owner = #{projectOwner}, purch_company = #{purchCompany}, project_total_amount = #{projectTotalAmount}, project_leader = #{projectLeader}, project_gross_margin = #{projectGrossMargin}, project_gross_profit = #{projectGrossProfit}, project_net_profit = #{projectNetProfit}, project_package_amount = #{projectPackageAmount}, project_desc = #{projectDesc}, project_status = #{projectStatus}, is_deleted = #{isDeleted}, create_by = #{createBy}, update_by = #{updateBy}, create_time = #{createTime}, update_time = #{updateTime}, check_content = #{checkContent}, file_name = #{fileName}, project_time_start = #{projectTimeStart}, project_time_end = #{projectTimeEnd}, project_check_status = #{projectCheckStatus}, project_ticket_status = #{projectTicketStatus}, project_contract_status = #{projectContractStatus}, project_acceptance_status = #{projectAcceptanceStatus}, project_dutypaid_status = #{projectDutypaidStatus}, place_code = #{placeCode}, purch_company_taxid = #{purchCompanyTaxid}, self_name = #{selfName}, ticket_type = #{ticketType}, ticket_tax = #{ticketTax},
where project_id = #{projectId}

update self_project
set is_deleted=0
where project_id = #{projectId}
